<?phpdate_default_timezone_set('UTC');function format_uptime($seconds) {	$uptimeString='';			  $secs = intval($seconds % 60);  $mins = intval($seconds / 60 % 60);  $hours = intval($seconds / 3600 % 24);  $days = intval($seconds / 86400);    if ($days > 0) {    $uptimeString .= $days;    $uptimeString .= (($days == 1) ? " день " : " дней ");  }  if ($hours > 0) {    $uptimeString .= (($days > 0) ? " " : "") . $hours;    $uptimeString .= (($hours == 1) ? " час" : " часов");  }  if ($mins > 0) {    $uptimeString .= (($days > 0 || $hours > 0) ? " " : "") . $mins;    $uptimeString .= (($mins == 1) ? " минута" : " минут");  }  if ($secs > 0) {    $uptimeString .= (($days > 0 || $hours > 0 || $mins > 0) ? " " : "") . $secs;    $uptimeString .= (($secs == 1) ? " секунда" : " секунд");  }  return $uptimeString;}function disktotal( $type  ){    if($type==1){   $bytes = disk_free_space("."); }    if($type==2){   $bytes = disk_total_space("."); }    if($type==3){   $bytes = disk_total_space(".")-disk_free_space("."); }    $si_prefix = array( 'B', 'KB', 'MB', 'GB', 'TB', 'EB', 'ZB', 'YB' );    $base = 1024;    $class = min((int)log($bytes , $base) , count($si_prefix) - 1);	$diskm = sprintf('%1.2f' , $bytes / pow($base,$class)) . ' ' . $si_prefix[$class];    return  $diskm;}function diskuse(){  $bytes =floor((disk_free_space(".")/disk_total_space("."))*100);     return  $bytes;}function parse_timestamp( $t = 0 ){ $month = floor( $t / 2592000 ); $day = ( $t / 86400 ) % 30; $hour = ( $t / 3600 ) % 24; $min = ( $t / 60 ) % 60; $sec = $t % 60;  $alm = $sec."сек";  if($min>0){$alm =$min."мин ".$alm;}   if($hour>0){$alm =$hour."ч ".$alm;}  if($day>0){$alm =$day."д ".$alm;} if($month>0){$alm =$month."м ".$alm;}     //$alm = $month."м ".$day."д ".$hour."ч ".$min."мин ".$sec."сек";   return  $alm;}function get_weather ($city, $col) { // Идентификатор города можно узнать по адресу http://weather.yandex.ru/static/cities.xml$data_file = 'http://export.yandex.ru/weather-ng/forecasts/'.$city.'.xml';   // Загружаем файл прогноза погоды для выбранного города$xml = simplexml_load_file($data_file); // загружаем xml файл через simple_xml$day_of_the_week_array = array(1 => 'Понедельник',2 => 'Вторник',3 => 'Среда',4 => 'Четверг',5 => 'Пятница',6 => 'Суббота',7 => 'Воскресенье');$time_of_day = array(0 => 'Утро',1 => 'День',2 => 'Ввечер',3 => 'Ночь'); $out = array(); // Массив вывода прогноза$counter = 0 ; // Счетчик количества дней, для которых доступен прогнозforeach ( $xml->day as $day )  {   if ($counter == $col) {break;}   $get_date = explode ("-" , $day['date']) ;   $day_of_week = date("N", mktime(0, 0, 0, $get_date[1], $get_date[2], $get_date[0])) ;   $out[$counter]['day'] = $get_date[2] ;   $out[$counter]['month'] = $get_date[1] ;   $out[$counter]['year'] = $get_date[0] ;   $out[$counter]['day_of_week'] = $day_of_the_week_array[$day_of_week] ;   for ($i=0;$i<=3;$i++) {   if($day->day_part[$i]->temperature == '') {   $get_temp_from =  $day->day_part[$i]->temperature_from;   $get_temp_to =  $day->day_part[$i]->temperature_to;    }  else {    $get_temp_from = (integer)$get_temp-1 ;    $get_temp_to = (integer)$get_temp+1 ;    }    if($get_temp_from>0 ) {$get_temp_from = '+'.$get_temp_from ; }    if($get_temp_to>0 ) {$get_temp_to = '+'.$get_temp_to ; }    $out[$counter]['weather'][$i]['temp_from'] = $get_temp_from;    $out[$counter]['weather'][$i]['temp_to'] = $get_temp_to;    $out[$counter]['weather'][$i]['image'] = $day->day_part[$i]->{'image-v3'};    $out[$counter]['weather'][$i]['time_of_day'] = $time_of_day[$i] ;} $counter++ ;}return $out ;}function monname( $mon  ){$months = Array(1 => 'Января', 'Февраля', 'Марта', 'Апреля', 'Мая', 'Июня','Июля', 'Августа', 'Сентября', 'Октября', 'Ноября', 'Декабря'); return  $months[intval($mon)];}function monnames( $mon  ){$months = Array(1 => 'Январь', 'Февраль', 'Март', 'Апрель', 'Май', 'Июнь','Июль', 'Август', 'Сентябрь', 'Октябрь', 'Ноябрь', 'Декабрь'); return  $months[intval($mon)];}?>